import React, { useState } from 'react';

function App() {
  const [gameStarted, setGameStarted] = useState(false);
  const [numPlayers, setNumPlayers] = useState(2);
  const [numRounds, setNumRounds] = useState(5);

  const handleStartGame = () => {
    setGameStarted(true);
  };

  // Test if Tailwind is working with a simple styled div
  return (
    <div className="p-4"> {/* Add padding to test if Tailwind works */}
      <div className="max-w-md mx-auto bg-white shadow-lg rounded-lg p-6">
        <h1 className="text-3xl font-bold mb-4">Fadu Card Game</h1>
        
        <div className="mb-4">
          <label className="block text-gray-700 mb-2">Number of Players:</label>
          <input
            type="number"
            min="2"
            max="8"
            value={numPlayers}
            onChange={(e) => setNumPlayers(parseInt(e.target.value))}
            className="w-full p-2 border rounded"
          />
        </div>

        <div className="mb-4">
          <label className="block text-gray-700 mb-2">Number of Rounds:</label>
          <input
            type="number"
            min="1"
            value={numRounds}
            onChange={(e) => setNumRounds(parseInt(e.target.value))}
            className="w-full p-2 border rounded"
          />
        </div>

        <button
          onClick={handleStartGame}
          className="w-full bg-blue-500 text-white p-2 rounded hover:bg-blue-600"
        >
          Start Game
        </button>
      </div>
    </div>
  );
}

export default App;
